CREATE SEQUENCE BERECHTIGUNG_SQ START WITH 2000000 INCREMENT BY 1;
CREATE SEQUENCE ROLLE_BERECHTIGUNG_SQ START WITH 2000000 INCREMENT BY 1;
CREATE SEQUENCE ROLLE_SQ START WITH 2000000 INCREMENT BY 1;
CREATE SEQUENCE USER_ROLLE_SQ START WITH 2000000 INCREMENT BY 1;
CREATE SEQUENCE USER_SQ START WITH 2000000 INCREMENT BY 1;
CREATE TABLE BERECHTIGUNG (BERECHTIGUNG_PK BIGINT NOT NULL, BEZEICHNUNG VARCHAR(100) NOT NULL, PRIMARY KEY (BERECHTIGUNG_PK), CONSTRAINT U_BRCHGNG_BEZEICHNUNG UNIQUE (BEZEICHNUNG));
CREATE TABLE ROLLE (ROLLE_PK BIGINT NOT NULL, BEZEICHNUNG VARCHAR(50) NOT NULL, PRIMARY KEY (ROLLE_PK), CONSTRAINT U_ROLLE_BEZEICHNUNG UNIQUE (BEZEICHNUNG));
CREATE TABLE ROLLE_BERECHTIGUNG (ROLLE_BERECHTIGUNG_PK BIGINT NOT NULL, BERECHTIGUNG_FK BIGINT NOT NULL, ROLLE_FK BIGINT NOT NULL, PRIMARY KEY (ROLLE_BERECHTIGUNG_PK), CONSTRAINT U_RLL_GNG_ROLLE_FK UNIQUE (ROLLE_FK, BERECHTIGUNG_FK));
CREATE TABLE USER (USER_PK BIGINT NOT NULL, AKTIV CHAR(1) NOT NULL, EMAIL VARCHAR(50) NOT NULL, NAME VARCHAR(50) NOT NULL, PASSWORT VARCHAR(255) NOT NULL, USERNAME VARCHAR(50) NOT NULL, PRIMARY KEY (USER_PK), CONSTRAINT U_USER_USERNAME UNIQUE (USERNAME));
CREATE TABLE USER_ROLLE (USER_ROLLE_PK BIGINT NOT NULL, ROLLE_FK BIGINT NOT NULL, USER_FK BIGINT NOT NULL, PRIMARY KEY (USER_ROLLE_PK), CONSTRAINT U_USR_RLL_USER_FK UNIQUE (USER_FK, ROLLE_FK));
ALTER TABLE ROLLE_BERECHTIGUNG ADD FOREIGN KEY (BERECHTIGUNG_FK) REFERENCES BERECHTIGUNG (BERECHTIGUNG_PK);
ALTER TABLE ROLLE_BERECHTIGUNG ADD FOREIGN KEY (ROLLE_FK) REFERENCES ROLLE (ROLLE_PK);
ALTER TABLE USER_ROLLE ADD FOREIGN KEY (ROLLE_FK) REFERENCES ROLLE (ROLLE_PK);
ALTER TABLE USER_ROLLE ADD FOREIGN KEY (USER_FK) REFERENCES USER (USER_PK);
